  const spread_factor_mid: f64 = 1 / <f64> (clipR_mid - clipL_mid) * 255
  const spread_factor_light: f32 = 1 / <f32> (clipR_light - clipL_light) * 255

  const exp_minus: f64 = Math.pow(2, -Math.abs(midAmount) * 0.01 * 5)
