export function lerp (a: f32, b: f32, f: f32): f32 {
  return  (a * (1.0 - f)) + (b * f)
}
function lerp_f64 (a: f64, b: f64, f: f64): f64 {
  return  (a * (1.0 - f)) + b * f
}
function lerp_clamped (a: f64, b: f64, f: f64): f64 {
  const result: f64 = a * (1.0 - f) + b * f
  if (result > 255) return 255
  if (result < 0) return 0
  return  result
}
function clampI32 (value_ptr: usize, min: i32, max: i32): void {
  if (load<i32>(value_ptr) < min)
    store<i32>(value_ptr, min)
  else if (load<i32>(value_ptr) > max)
    store<i32>(value_ptr, max)
}

function normalize2rgb (oMin: f32, oMax: f32, dMin: f32, dMax: f32, value: f32): f32 {
  return <f32> ((value - oMin) / (oMax - oMin) * (dMax - dMin) + dMin)
}

function findPercentage (number: f32, a: f32, b: f32): f32 {
  return (number - a) / (b - a)
}

function averageCompressed (r: i32, g: i32, b: i32, divider: f64): i32 {
  return <i32> ((r * 0.2126 + g * 0.7152 + b * 0.0722) * divider)
}

